// metrics.middleware.ts

import { Injectable, NestMiddleware } from '@nestjs/common';
import { Request, Response, NextFunction } from 'express';
import { Counter, Histogram } from 'prom-client';

@Injectable()
export class MetricsMiddleware implements NestMiddleware {
  private readonly requestCounter: Counter<string>;
  private readonly responseTimeHistogram: Histogram<string>;
  private readonly errorCounter: Counter<string>;

  constructor() {
    this.requestCounter = new Counter({
      name: 'viiv_requests_total',
      help: 'Total number of HTTP requests',
      labelNames: ['method', 'route'],
    });

    this.responseTimeHistogram = new Histogram({
      name: 'viiv_response_time_seconds',
      help: 'Duration of HTTP requests in seconds',
      labelNames: ['method', 'route', 'status_code'],
    });

    this.errorCounter = new Counter({
      name: 'viiv_errors_total',
      help: 'Total number of HTTP errors',
      labelNames: ['method', 'route', 'status_code'],
    });
  }

  use(req: Request, res: Response, next: NextFunction) {
    const end = this.responseTimeHistogram.startTimer({
      method: req.method,
      route: req.route?.path || req.path,
    });

    res.on('finish', () => {
      const route = req.route?.path || req.path;
      this.requestCounter.labels(req.method, route).inc();
      end({
        method: req.method,
        route,
        status_code: res.statusCode.toString(),
      });

      if (res.statusCode >= 400) {
        this.errorCounter
          .labels(req.method, route, res.statusCode.toString())
          .inc();
      }
    });

    next();
  }
}
