  CPF = 'CPF',
  CNPJ = 'CNPJ',
}

export class CreatePixDto {
  @IsString()
  @IsNotEmpty()
  @MaxLength(32, { message: 'App ID must not exceed 32 characters.' })
  app_id: string;

  @IsOptional()
  @IsString()
  duration?: string;

  @IsOptional()
  @IsString()
  customer_phone?: string;

  @IsString()
  @IsNotEmpty()
  customer_email: string;

  @IsString()
  @IsNotEmpty()
  @Matches(/^\d{11}$|^\d{14}$/, {
    message:
      'Customer identification number must be 11 digits for CPF or 14 digits for CNPJ.',
  })
  customer_identify_number: string;

  @IsString()
  @IsEnum(IdentificationType, {
    message: 'Customer identification type must be either CPF or CNPJ.',
  })
  customer_identify_type: IdentificationType;

  @IsString()
  @IsNotEmpty()
  customer_name: string;

  @IsString()
  @IsNotEmpty()
  method: string;

  @IsString()
  @IsNotEmpty()
  @MaxLength(64, { message: 'Out trade number must not exceed 64 characters.' })
  out_trade_no: string;

  @IsString()
  @IsNotEmpty()
  notify_url: string;

  @IsString()
  @IsNotEmpty()
  @Matches(/^\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}$/, {
    message: 'Timestamp must follow the format yyyy-MM-dd HH:mm:ss.',
  })
  timestamp: string;

  @IsString()
  @IsNotEmpty()
  @MaxLength(128, { message: 'Subject must not exceed 128 characters.' })
