import {
  Controller,
  Get,
  Post,
  Headers,
  Body,
  BadRequestException,
} from '@nestjs/common';
import { PixService } from './services/pix.service';
import { CreatePixDto } from './dtos/viiv-create-pix.dto';

@Controller('payin')
export class PayinController {
  constructor(private readonly pixService: PixService) {} // Inject PixService

  // Pix service handler
  @Post('pix')
  async createPixPayin(@Body() body: CreatePixDto) {
    // Validate method is PIX (fixed value)
    if (body.method !== 'PIX') {
      throw new BadRequestException('Method must be "PIX".');
    }

    // Validate order currency (fixed value)
    if (body.order_currency !== 'BRL') {
      throw new BadRequestException('Order currency must be "BRL".');
    }
    // Should be bank agnostic
    const externalResponse = await this.pixService.callExternalPixApi(body);

    // Process and return response
    return {
      message: 'PIX payment processed',
      data: externalResponse,
    };
  }

  @Get('test')
  // nest have dedicated decoratos for capturing headers, body, query, etc
  // or we can use @Req decorator to access the underlying HTTP platform
  async testStuff(@Headers() headers: object) {
    // Nest will convert this to json (objects and arrays)
    return headers;
  }
}
