// Auth abstraction service (currently only BB, but this must be abstracted)
import { Injectable } from '@nestjs/common';
import { HttpService } from '@nestjs/axios';
import { firstValueFrom } from 'rxjs';
import { ConfigService } from '@nestjs/config';

@Injectable()
export class AuthService {
  private accessToken: string;
  private tokenExpiresAt: number; // Timestamp when the token expires

  constructor(
    private readonly httpService: HttpService,
    private readonly configService: ConfigService,
  ) {}

  async getAccessToken(): Promise<string> {
    if (this.isTokenValid()) {
      return this.accessToken;
    }
    return this.fetchNewToken();
  }

  private isTokenValid(): boolean {
    // Check if the token exists and hasn't expired
    return !!this.accessToken && Date.now() < this.tokenExpiresAt;
  }

  private async fetchNewToken(): Promise<string> {
    console.log('Fetching new token...'); // Add this line
    const authHeader = this.generateBasicAuthHeader();
    const params = new URLSearchParams();
    params.append('grant_type', 'client_credentials');
    params.append('scope', 'cob.write');

    try {
      const response = await firstValueFrom(
        this.httpService.post(
          'https://oauth.hm.bb.com.br/oauth/token',
          params,
          {
            headers: {
              Authorization: authHeader,
              'Content-Type': 'application/x-www-form-urlencoded',
            },
          },
        ),
      );

      // Validate response
      if (!response.data.access_token || !response.data.expires_in) {
        throw new Error('Invalid OAuth response');
      }

      // Set token and expiration time (with a 10-second buffer to avoid edge cases)
      this.accessToken = response.data.access_token;
      this.tokenExpiresAt =
        Date.now() + response.data.expires_in * 1000 - 10000; // 10 seconds buffer
      // Log the new access token
      console.log(
        `New token acquired. Valid until: ${new Date(this.tokenExpiresAt).toISOString()}`,
      );

      return this.accessToken;
    } catch (error) {
      console.error(
        'OAuth2 token request failed:',
        error.response?.data || error.message,
      );
      throw new Error('Authentication failed');
    }
  }

  private generateBasicAuthHeader(): string {
    const clientId = this.configService.get<string>('BB_CLIENT_ID');
    const clientSecret = this.configService.get<string>('BB_CLIENT_SECRET');

    if (!clientId || !clientSecret) {
      throw new Error(
        'Banco do Brasil credentials are missing. Check your .env file.',
      );
    }

    const credentials = Buffer.from(`${clientId}:${clientSecret}`).toString(
      'base64',
    );
    return `Basic ${credentials}`;
  }
}
