/**
 * @file Maps between the payload our API receives and the payload we need to send to every bank.
 */
import { CreatePixDto } from '../../dtos/viiv-create-pix.dto';
import { BancoDoBrasilPixRequestDto } from '../../dtos/bb-pix-request.dto';

export class PixMapper {
  // what we need to send to banco do brasil
  static toBancoDoBrasil(
    createPixDto: CreatePixDto,
  ): BancoDoBrasilPixRequestDto {
    return {
      calendario: {
        expiracao: Number(createPixDto.duration) || 8600, // in seconds, 86400 = 24hrs, should be dynamic
      },
      devedor: {
        cpf: createPixDto.customer_identify_number,
        nome: createPixDto.customer_name,
      },
      valor: {
        original: createPixDto.order_amount, // VALIDAR!
        modalidadeAlteracao: 0, // Example: default to no changes allowed
      },
      chave: '95127446000198', // Eg. 95127446000198 or hmtestes2@bb.com.br. Our key!
      solicitacaoPagador: createPixDto.subject,
    };
  }

  // Other mappers for different banks here (e.g., toSantander, toItau)
}
