@Injectable()
export class PixService {
  constructor(
    private readonly configService: ConfigService, // for .env variables
    private readonly authService: AuthService,
    private readonly httpService: HttpService,
  ) {}

  async callExternalPixApi(createPixDto: CreatePixDto): Promise<any> {
    const gwDevAppKey = this.configService.get<string>('BB_DEV_APP_KEY');
    // console.log(`Log the Dev API Key: ${gwDevAppKey}`);

    // Generate the access token via the authService
    const accessToken = await this.authService.getAccessToken();

    // Add logging to debug headers
    console.log('Authorization Header:', `Bearer ${accessToken}`);

    // Transform to the bank-specific DTO
    const bbPayload: BancoDoBrasilPixRequestDto =
      PixMapper.toBancoDoBrasil(createPixDto);

    // Log the transformed payload for debugging
    console.log('Sending to Banco do Brasil:', bbPayload);
    const txId = `${generateRandomTxid()}`; // eg testenico000001231r11241449
    console.log(`Transaction id: ${txId}`);

    try {
      const response = await firstValueFrom(
        // urls must depend on the run context (dev or prod)
        this.httpService.put(
          `https://api.hm.bb.com.br/pix/v2/cob/${txId}`,
          bbPayload,
          {
            // for future certificates
            // httpsAgent: new https.Agent({
            //   cert: fs.readFileSync('./client-cert.pem'),
            //   key: fs.readFileSync('./client-key.pem'),
            // }),
            headers: {
              Authorization: `Bearer ${accessToken}`,
              'Content-Type': 'application/json',
            },
            params: {
              'gw-dev-app-key': gwDevAppKey, // Add as query parameter
            },
          },
        ),
      );
      console.log('PIX GENERATED OK!');
      // RETURN VALUES FOR PIX MUST BE STANDARIZED VIA A TYPESCRIPT INTERFACE
      return response.data;
    } catch (error) {
      // console.error('PIX API call failed:', error.response?.data);
      // throw new Error('PIX payment processing failed');
      // Log the full error for debugging
      // console.error('PIX API Error:', {
      //   status: error.response?.status,
      //   data: error.response?.data,
      //   headers: error.response?.headers,
      // });
      console.error('PIX API ERROR:', error);
      throw new Error('PIX payment processing failed');
    }
  }
}
