2. **Update the `callExternalPixApi` Method to Handle PIX-Specific Errors:**

```typescript
// src/payin/services/pix.service.ts
import { Injectable } from '@nestjs/common';
import { firstValueFrom, catchError, throwError } from 'rxjs';
import { ConfigService } from '@nestjs/config';

// utils
import { generateRandomTxid } from '$utils/index';

// dtos
import { CreatePixDto } from '../dtos/viiv-create-pix.dto';
import { BancoDoBrasilPixRequestDto } from '../dtos/bb-pix-request.dto';

// services
import { AuthService } from './auth.service';
import { HttpService } from '@nestjs/axios';
import { PixMapper } from './mappers/pix.mapper';
import { PixServiceException } from './exceptions/pix.service.exception';

@Injectable()
export class PixService {
  constructor(
    private readonly configService: ConfigService, // for .env variables
    private readonly authService: AuthService,
    private readonly httpService: HttpService,
  ) {}

  async callExternalPixApi(createPixDto: CreatePixDto): Promise<any> {
    const gwDevAppKey = this.configService.get<string>('BB_DEV_APP_KEY');
    // console.log(`Log the Dev API Key: ${gwDevAppKey}`);

    // Generate the access token via the authService
    const accessToken = await this.authService.getAccessToken();

    // Add logging to debug headers
    console.log('Authorization Header:', `Bearer ${accessToken}`);

    // Transform to the bank-specific DTO
    const bbPayload: BancoDoBrasilPixRequestDto =
      PixMapper.toBancoDoBrasil(createPixDto);

    // Log the transformed payload for debugging
    console.log('Sending to Banco do Brasil:', bbPayload);
    const txId = `${generateRandomTxid()}`; // eg testenico000001231r11241449
    console.log(`Transaction id: ${txId}`);

    try {
      const response = await firstValueFrom(
        this.httpService.put(
          `https://api.hm.bb.com.br/pix/v2/cob/${txId}`,
          bbPayload,
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
              'Content-Type': 'application/json',
            },
            params: {
              'gw-dev-app-key': gwDevAppKey,
            },
          },
        ).pipe(
          catchError((error) => {
            if (error.response && error.response.status === 400) {
              throw new PixServiceException('Invalid request', ERROR_CODES.PIX_SERVICE_ERROR);
            } else if (error.response && error.response.status === 500) {
              throw new PixServiceException('Server error', ERROR_CODES.SERVICE_ERROR);
            }
            return throwError(error);
          }),
        ),
      );
      console.log('PIX GENERATED OK!');
      return response.data;
    } catch (error) {
      if (error instanceof PixServiceException) {
        throw error;
      } else {
        throw new PixServiceException('PIX payment processing failed', ERROR_CODES.SERVICE_ERROR);
      }
    }
  }
}
```

